import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './CourseApplicants.css';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const ApplicantsTable = () => {
  const [applicants, setApplicants] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchApplicants();
  }, []);

  const fetchApplicants = async () => {
    try {
      const res = await axios.get(`${API_BASE_URL}/course-applicants`); // Adjust endpoint if necessary
      setApplicants(res.data);
    } catch (err) {
      console.error('Error fetching applicants:', err);
    }
  };

  const filteredApplicants = applicants.filter(applicant =>
    applicant.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (applicant.email && applicant.email.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (applicant.mobile && applicant.mobile.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (applicant.institute && applicant.institute.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (applicant.courseName && applicant.courseName.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="applicants-table-page">
      <h1>Course Applicants</h1>

      <div className="controls">
        <input
          type="text"
          placeholder="Search by name, email, mobile, institute or course"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Mobile Number</th>
            <th>Institute</th>
            <th>Course Name</th>
          </tr>
        </thead>
        <tbody>
          {filteredApplicants.length === 0 ? (
            <tr>
              <td colSpan="6" style={{ textAlign: 'center' }}>No applicants found.</td>
            </tr>
          ) : (
            filteredApplicants.map(applicant => (
              <tr key={applicant._id}>
                <td>{applicant._id}</td>
                <td>{applicant.name}</td>
                <td>{applicant.email}</td>
                <td>{applicant.mobile}</td>
                <td>{applicant.institute}</td>
                <td>{applicant.courseName}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ApplicantsTable;
