import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Assets.css';

const Assets = () => {
  const [totalCost, setTotalCost] = useState(0);
  const [totalQuantity, setTotalQuantity] = useState(0);

  useEffect(() => {
    const fetchTotalCost = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/inventory/total-cost');
        setTotalCost(res.data.totalCost);
        setTotalQuantity(res.data.totalQuantity);
      } catch (err) {
        console.error(err);
      }
    };

    fetchTotalCost();
  }, []);

  return (
    <div className="assets">
      <h1>Assets Page</h1>
      <div className="total-cost-block">
        <h2>Total Inventory Cost</h2>
        <p>â‚¹ {totalCost.toFixed(2)}</p>
      </div>
      <div className="total-quantity-block">
        <h2>Total Quantity</h2>
        <p>{totalQuantity}</p>
      </div>
    </div>
  );
};

export default Assets;
