import React from 'react';
import Sidebar from './Sidebar';
import './Layout.css'; // New CSS for layout spacing

const Layout = ({ children }) => {
  return (
    <div className="layout">
      <Sidebar />
      <div className="main-content">
        {children}
      </div>
    </div>
  );
};

export default Layout;
