import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Inventory.css';

const API_URL = 'http://localhost:5000/api/inventory';

const Inventory = () => {
  const [product, setProduct] = useState({
    product_id: '',
    name: '',
    quantity: '',
    costPerUnit: '',
    totalCost: '',
    description: ''
  });

  const [items, setItems] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentId, setCurrentId] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  const userRole = localStorage.getItem('role'); // ✅ get role from localStorage

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    const res = await axios.get(API_URL);
    setItems(res.data);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    let updatedProduct = { ...product, [name]: value };

    if (name === 'quantity' || name === 'costPerUnit') {
      const quantity = name === 'quantity' ? value : updatedProduct.quantity;
      const costPerUnit = name === 'costPerUnit' ? value : updatedProduct.costPerUnit;
      const totalCost = parseFloat(quantity) * parseFloat(costPerUnit);
      updatedProduct.totalCost = !isNaN(totalCost) ? totalCost.toFixed(2) : '';
    }

    setProduct(updatedProduct);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (isEditing) {
        if (userRole === 'admin') {
          await axios.put(`${API_URL}/${currentId}`, product);
        } else {
          alert('You do not have permission to update.');
          return;
        }
      } else {
        await axios.post(API_URL, product);
      }

      alert(isEditing ? 'Product updated!' : 'Product added!');

      setProduct({
        product_id: '',
        name: '',
        quantity: '',
        costPerUnit: '',
        totalCost: '',
        description: ''
      });

      setIsEditing(false);
      setCurrentId(null);
      fetchItems();
    } catch (err) {
      if (err.response && err.response.status === 400) {
        alert(err.response.data.message);
      } else {
        console.error(err);
        alert('Something went wrong.');
      }
    }
  };

  const handleDelete = async (id) => {
    if (userRole !== 'admin') {
      alert('You do not have permission to delete.');
      return;
    }

    await axios.delete(`${API_URL}/${id}`);
    fetchItems();
  };

  const handleEdit = (item) => {
    if (userRole !== 'admin') {
      alert('You do not have permission to edit.');
      return;
    }

    setProduct(item);
    setIsEditing(true);
    setCurrentId(item._id);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // ✅ Filter items by product_id or name
  const filteredItems = items.filter(item =>
    item.product_id.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="inventory">
      <h1>Inventory Management</h1>

      <form className="inventory-form" onSubmit={handleSubmit}>
        <label>Product ID:</label>
        <input
          type="text"
          name="product_id"
          value={product.product_id}
          onChange={handleChange}
          required
        />

        <label>Product Name:</label>
        <input
          type="text"
          name="name"
          value={product.name}
          onChange={handleChange}
          required
        />

        <label>Quantity:</label>
        <input
          type="number"
          name="quantity"
          value={product.quantity}
          onChange={handleChange}
          required
        />

        <label>Cost Per Unit:</label>
        <input
          type="number"
          name="costPerUnit"
          value={product.costPerUnit}
          onChange={handleChange}
          required
        />

        <label>Total Cost:</label>
        <input
          type="text"
          name="totalCost"
          value={product.totalCost}
          readOnly
        />

        <label>Description:</label>
        <textarea
          name="description"
          value={product.description}
          onChange={handleChange}
        ></textarea>

        <button type="submit">{isEditing ? 'Update Product' : 'Add Product'}</button>
      </form>

      <div className="inventory-search">
        <label>Search by Product ID or Name:</label>
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Type to search..."
        />
      </div>

      <div className="inventory-table">
        <h2>Inventory Items</h2>
        {filteredItems.length === 0 ? (
          <p>No items found.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Product ID</th>
                <th>Name</th>
                <th>Quantity</th>
                <th>Cost Per Unit</th>
                <th>Total Cost</th>
                <th>Description</th>
                {userRole === 'admin' && <th>Actions</th>}
              </tr>
            </thead>
            <tbody>
              {filteredItems.map((item) => (
                <tr key={item._id}>
                  <td>{item.product_id}</td>
                  <td>{item.name}</td>
                  <td>{item.quantity}</td>
                  <td>{item.costPerUnit}</td>
                  <td>{item.totalCost}</td>
                  <td>{item.description}</td>
                  {userRole === 'admin' && (
                    <td>
                      <button className="edit-btn" onClick={() => handleEdit(item)}>Edit</button>
                      <button className="delete-btn" onClick={() => handleDelete(item._id)}>Delete</button>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Inventory;
