import React, { useState } from 'react';
import jsPDF from 'jspdf';
import 'jspdf-autotable'; // ✅ Make sure this is installed!
import logo from '../assets/logo1.png'; // Update path if needed
import './Quotation.css';

const Quotation = () => {
  const [quotationData, setQuotationData] = useState({
    customerName: '',
    product: '',
    quantity: '',
    price: '',
  });

  const handleChange = (e) => {
    setQuotationData({
      ...quotationData,
      [e.target.name]: e.target.value,
    });
  };

  const handleGeneratePDF = () => {
    const doc = new jsPDF();

    const img = new Image();
    img.src = logo;

    img.onload = () => {
      // ✅ Logo top-left
      doc.addImage(img, 'PNG', 10, 10, 40, 40);

      // ✅ Watermark
      doc.setGState(new doc.GState({ opacity: 0.05 }));
      doc.addImage(img, 'PNG', 40, 80, 120, 120);
      doc.setGState(new doc.GState({ opacity: 1 }));

      // ✅ Title
      doc.setFontSize(16);
      doc.text('Indian Scientific Aerospace and Robotics', 60, 20);

      doc.setFontSize(14);
      doc.text('Quotation', 20, 60);

      // ✅ Customer name
      doc.setFontSize(12);
      doc.text(`Customer: ${quotationData.customerName}`, 20, 70);

      // ✅ Table
      doc.autoTable({
        startY: 80,
        head: [['Product', 'Quantity', 'Price per unit', 'Total']],
        body: [
          [
            quotationData.product,
            quotationData.quantity,
            quotationData.price,
            Number(quotationData.quantity) * Number(quotationData.price)
          ]
        ],
      });

      doc.save('quotation.pdf');
    };
  };

  return (
    <div className="quotation">
      <h1>Quotation Page</h1>

      <form className="quotation-form" onSubmit={(e) => e.preventDefault()}>
        <label>Customer Name:</label>
        <input
          type="text"
          name="customerName"
          value={quotationData.customerName}
          onChange={handleChange}
          required
        />

        <label>Product:</label>
        <input
          type="text"
          name="product"
          value={quotationData.product}
          onChange={handleChange}
          required
        />

        <label>Quantity:</label>
        <input
          type="number"
          name="quantity"
          value={quotationData.quantity}
          onChange={handleChange}
          required
        />

        <label>Price per unit:</label>
        <input
          type="number"
          name="price"
          value={quotationData.price}
          onChange={handleChange}
          required
        />

        <button type="button" onClick={handleGeneratePDF}>Generate PDF</button>
      </form>
    </div>
  );
};

export default Quotation;
