import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Salary = () => {
  const [salaryData, setSalaryData] = useState([]);

  useEffect(() => {
    axios.get('/api/salary') // adjust URL to match your backend
      .then((res) => setSalaryData(res.data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="salary-page">
      <h1>Employee Salary Details</h1>
      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Name</th>
            <th>Bank</th>
            <th>Account No</th>
            <th>IFSC</th>
            <th>Branch</th>
            <th>Days Worked</th>
            <th>Wage/Day</th>
            <th>Total Salary</th>
          </tr>
        </thead>
        <tbody>
          {salaryData.map((item, index) => (
            <tr key={item._id}>
              <td>{index + 1}</td>
              <td>{item.name}</td>
              <td>{item.bank}</td>
              <td>{item.accountNo}</td>
              <td>{item.ifsc}</td>
              <td>{item.branch}</td>
              <td>{item.daysWorked}</td>
              <td>{item.wagePerDay}</td>
              <td>{item.daysWorked * item.wagePerDay}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Salary;
