import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  ArcElement,
  Tooltip,
  Legend,
} from 'chart.js';
import './Dashboard.css';

ChartJS.register(BarElement, CategoryScale, LinearScale, ArcElement, Tooltip, Legend);

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const Dashboard = () => {
  const [inventory, setInventory] = useState([]);
  const [billsCount, setBillsCount] = useState(0);

  const totalCost = inventory.reduce(
    (acc, item) => acc + item.quantity * item.costPerUnit,
    0
  );
  const totalQuantity = inventory.reduce((acc, item) => acc + item.quantity, 0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const inventoryRes = await axios.get(`${API_BASE_URL}/api/inventory`);
        setInventory(inventoryRes.data);

        const billsRes = await axios.get(`${API_BASE_URL}/api/bills`);
        setBillsCount(billsRes.data.length);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, []);

  const barData = {
    labels: inventory.map((item) => item.name),
    datasets: [
      {
        label: 'Quantity',
        data: inventory.map((item) => item.quantity),
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
      },
    ],
  };

  const pieData = {
    labels: inventory.map((item) => item.name),
    datasets: [
      {
        label: 'Cost Distribution',
        data: inventory.map((item) => item.quantity * item.costPerUnit),
        backgroundColor: [
          '#FF6384',
          '#36A2EB',
          '#FFCE56',
          '#4BC0C0',
          '#9966FF',
          '#FF9F40',
        ],
      },
    ],
  };

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>

      <div className="stats">
        <div className="stat">
          <h2>Total Inventory Cost</h2>
          <p>â‚¹ {totalCost.toFixed(2)}</p>
        </div>
        <div className="stat">
          <h2>Total Quantity</h2>
          <p>{totalQuantity}</p>
        </div>
        <div className="stat">
          <h2>Total Bills</h2>
          <p>{billsCount}</p>
        </div>
      </div>

      <div className="charts">
        <div className="chart">
          <h3>Quantity per Product</h3>
          <Bar data={barData} />
        </div>

        <div className="chart">
          <h3>Cost Distribution</h3>
          <Pie data={pieData} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
