import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Sidebar.css';
import { FiSettings } from 'react-icons/fi';

const Sidebar = () => {
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);

  const role = localStorage.getItem('role');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/');
    setIsOpen(false);
  };

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleLinkClick = () => {
    setIsOpen(false);
  };

  return (
    <>
      <div className="hamburger" onClick={handleToggle}>
        ☰
      </div>

      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <h2>ISAR Accounts</h2>

        <div className="sidebar-content">
          <ul className="nav-links">
            <li><Link to="/dashboard" onClick={handleLinkClick}>Dashboard</Link></li>
            <li><Link to="/bills" onClick={handleLinkClick}>Bills</Link></li>
            <li><Link to="/inventory" onClick={handleLinkClick}>Inventory</Link></li>
            <li><Link to="/assets" onClick={handleLinkClick}>Assets</Link></li>
            <li><Link to="/Salary" onClick={handleLinkClick}>Salary</Link></li>
            <li><Link to="/quotation" onClick={handleLinkClick}>Quotation</Link></li>
          </ul>

          <ul className="bottom-links">
            {role === 'admin' && (
              <li> <Link to="/settings" onClick={handleLinkClick} title="Settings">
                <FiSettings size={30} /> {/* ✅ Settings icon only */}
              </Link></li>
            )}
            <li><button onClick={handleLogout}>Logout</button></li>
          </ul>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
