import React, { useState } from 'react';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import logo from '../assets/logo1.png';
import './Quotation.css';

const Quotation = () => {
  const [quotationData, setQuotationData] = useState({
    customerName: '',
    customerAddress: '',
    items: [
      { product: '', description: '', quantity: 1, price: 0 },
    ],
    preparedBy: '',
  });

  const handleItemChange = (index, field, value) => {
    const newItems = [...quotationData.items];
    newItems[index][field] = value;
    setQuotationData({ ...quotationData, items: newItems });
  };

  const handleAddItem = () => {
    setQuotationData({
      ...quotationData,
      items: [...quotationData.items, { product: '', description: '', quantity: 1, price: 0 }],
    });
  };

  const handleRemoveItem = (index) => {
    const newItems = quotationData.items.filter((_, i) => i !== index);
    setQuotationData({ ...quotationData, items: newItems });
  };

  const handleChange = (e) => {
    setQuotationData({ ...quotationData, [e.target.name]: e.target.value });
  };

  const handleGeneratePDF = () => {
    const doc = new jsPDF();

    const img = new Image();
    img.src = logo;

    img.onload = () => {
      doc.addImage(img, 'PNG', 10, 10, 40, 40);

      doc.setGState(new doc.GState({ opacity: 0.05 }));
      doc.addImage(img, 'PNG', 50, 100, 120, 120);
      doc.setGState(new doc.GState({ opacity: 1 }));

      doc.setFontSize(16);
      doc.text('Indian Scientific Aerospace and Robotics', 60, 20);
      doc.setFontSize(10);
      doc.text('339/2, Kurunji Nagar, Valayapatti, Mohanur,', 60, 28);
      doc.text('Namakkal district, Tamil Nadu 637020', 60, 34);
      doc.text('Phone: +91 6374720788 | Email: admin@isaar.in', 60, 40);

      doc.setFontSize(12);
      doc.text(`Date: ${new Date().toLocaleDateString()}`, 150, 28);

      doc.text(`Customer Name: ${quotationData.customerName}`, 20, 60);
      doc.text(`Address: ${quotationData.customerAddress}`, 20, 68);

      const tableBody = quotationData.items.map((item, i) => [
        i + 1,
        item.product,
        item.description,
        item.quantity,
        `₹${item.price}`,
        `₹${item.quantity * item.price}`,item.description
        
      ]);

      const subtotal = quotationData.items.reduce((acc, item) => acc + item.quantity * item.price, 0);
      const gst = subtotal * 0.18;
      const total = subtotal + gst;

      autoTable(doc, {
  startY: 80,
  head: [['S.No', 'Product/Item', 'Description', 'Quantity', 'Rate per Unit', 'Total Price']],
  body: tableBody,
  columnStyles: {
    2: { cellWidth: 60 }, // Description column (index 2)
  },
  styles: {
    overflow: 'linebreak',
    cellWidth: 'wrap',
  },
});

      let finalY = doc.lastAutoTable.finalY + 10;

      doc.text(`Subtotal: ₹${subtotal.toFixed(2)}`, 120, finalY);
      finalY += 6;
      doc.text(`GST (18%): ₹${gst.toFixed(2)}`, 120, finalY);
      finalY += 6;
      doc.text(`Grand Total: ₹${total.toFixed(2)}`, 120, finalY);
       // Terms & Conditions
      /*finalY += 15;
      doc.setFontSize(10);
      doc.text('Terms & Conditions:', 20, finalY);
      finalY += 5;
      doc.text('• Delivery Time: Within 5 business days after confirmation.', 20, finalY);
      finalY += 5;
      doc.text('• Payment Terms: 50% advance, balance on delivery.', 20, finalY);
      finalY += 5;
      doc.text('• Validity: This quote is valid for 15 days.', 20, finalY);
      finalY += 5;
      doc.text('• Warranty: 1 year on hardware products.', 20, finalY);*/

      finalY += 10;
      doc.text(`Prepared By: ${quotationData.preparedBy || '[Your Name]'}`, 20, finalY);
      finalY += 5;
      doc.text(`Designation: Sales Manager`, 20, finalY);
      finalY += 15;
      doc.text(`Signature: ____________________`, 20, finalY);

      doc.save('quotation.pdf');
    };
  };

  return (
    <div className="quotation">
      <h1>Quotation Page</h1>

      <form className="quotation-form" onSubmit={(e) => e.preventDefault()}>
        <label>Customer Name:</label>
        <input
          type="text"
          name="customerName"
          value={quotationData.customerName}
          onChange={handleChange}
          required
        />

        <label>Customer Address:</label>
        <textarea
          name="customerAddress"
          value={quotationData.customerAddress}
          onChange={handleChange}
          required
        />

        {quotationData.items.map((item, index) => (
          <div key={index} className="item-row">
            <label>Product:</label>
            <input
              type="text"
              value={item.product}
              onChange={(e) => handleItemChange(index, 'product', e.target.value)}
              required
            />

            <label>Description:</label>
            <textarea
              value={item.description}
              onChange={(e) => handleItemChange(index, 'description', e.target.value)}
            />

            <label>Quantity:</label>
            <input
              type="number"
              value={item.quantity}
              onChange={(e) => handleItemChange(index, 'quantity', parseInt(e.target.value))}
              required
            />

            <label>Price per unit:</label>
            <input
              type="number"
              value={item.price}
              onChange={(e) => handleItemChange(index, 'price', parseFloat(e.target.value))}
              required
            />

            {quotationData.items.length > 1 && (
              <button type="button" onClick={() => handleRemoveItem(index)}>
                Remove Item
              </button>
            )}
          </div>
        ))}

        <button type="button" onClick={handleAddItem}>+ Add Item</button>

        <label>Prepared By:</label>
        <input
          type="text"
          name="preparedBy"
          value={quotationData.preparedBy}
          onChange={handleChange}
        />

        <button type="button" onClick={handleGeneratePDF}>Generate PDF</button>
      </form>
    </div>
  );
};

export default Quotation;
