import React, { useState } from 'react';
import jsPDF from 'jspdf';
import logo from '../assets/logo1.png'; // ✅ adjust the path to match where your logo is!
import './Quotation.css';

const Quotation = () => {
  const [quotationData, setQuotationData] = useState({
    customerName: '',
    product: '',
    quantity: '',
    price: '',
  });

  const handleChange = (e) => {
    setQuotationData({
      ...quotationData,
      [e.target.name]: e.target.value,
    });
  };

  const handleGeneratePDF = () => {
    const doc = new jsPDF();

    // ✅ Add logo
    const img = new Image();
    img.src = logo;

    img.onload = function () {
      doc.addImage(img, 'PNG', 150, 10, 40, 40);

      // ✅ Add title below logo
      doc.setFontSize(18);
      doc.text('Indian Scientific Aerospace and Robotics', 20, 30);

      doc.setFontSize(16);
      doc.text('Quotation', 20, 50);

      doc.setFontSize(12);
      doc.text(`Customer: ${quotationData.customerName}`, 20, 70);
      doc.text(`Product: ${quotationData.product}`, 20, 80);
      doc.text(`Quantity: ${quotationData.quantity}`, 20, 90);
      doc.text(`Price per unit: ${quotationData.price}`, 20, 100);

      const total = Number(quotationData.quantity) * Number(quotationData.price);
      doc.text(`Total: ${total}`, 20, 110);

      doc.save('quotation.pdf');
    };
  };

  return (
    <div className="quotation">
      <h1>Quotation Page</h1>

      <form className="quotation-form" onSubmit={(e) => e.preventDefault()}>
        <label>Customer Name:</label>
        <input
          type="text"
          name="customerName"
          value={quotationData.customerName}
          onChange={handleChange}
          required
        />

        <label>Product:</label>
        <input
          type="text"
          name="product"
          value={quotationData.product}
          onChange={handleChange}
          required
        />

        <label>Quantity:</label>
        <input
          type="number"
          name="quantity"
          value={quotationData.quantity}
          onChange={handleChange}
          required
        />

        <label>Price per unit:</label>
        <input
          type="number"
          name="price"
          value={quotationData.price}
          onChange={handleChange}
          required
        />

        <button type="button" onClick={handleGeneratePDF}>Generate PDF</button>
      </form>
    </div>
  );
};

export default Quotation;
