import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './CareerApplicants.css';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const Salary = () => {
  const [salaryData, setSalaryData] = useState([]);
  const [formData, setFormData] = useState({
    employee_id: '',
    name: '',
    bank: '',
    accountNo: '',
    ifsc: '',
    branch: '',
    daysWorked: '',
    wagePerDay: ''
  });
  const [editingId, setEditingId] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchSalary();
  }, []);

  const fetchSalary = () => {
    axios.get(`${API_BASE_URL}/salary`)
      .then((res) => setSalaryData(res.data))
      .catch((err) => console.error(err));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (editingId) {
      const confirmUpdate = window.confirm("Are you sure you want to update this employee's details?");
      if (!confirmUpdate) return;

      try {
        await axios.put(`${API_BASE_URL}/salary/${editingId}`, formData);
      } catch (err) {
        console.error('Error updating salary:', err);
      }
    } else {
      // Prevent duplicates
      const exists = salaryData.find(emp => emp.employee_id === formData.employee_id);
      if (exists) {
        window.alert("Employee ID already exists!");
        return;
      }

      try {
        await axios.post(`${API_BASE_URL}/salary`, formData);
      } catch (err) {
        console.error('Error saving salary:', err);
      }
    }

    fetchSalary();
    setFormData({
      employee_id: '',
      name: '',
      bank: '',
      accountNo: '',
      ifsc: '',
      branch: '',
      daysWorked: '',
      wagePerDay: ''
    });
    setEditingId(null);
  };

  const handleEdit = (item) => {
    setFormData({
      employee_id: item.employee_id,
      name: item.name,
      bank: item.bank,
      accountNo: item.accountNo,
      ifsc: item.ifsc,
      branch: item.branch,
      daysWorked: item.daysWorked,
      wagePerDay: item.wagePerDay
    });
    setEditingId(item._id);
  };

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this employee?");
    if (!confirmDelete) return;

    try {
      await axios.delete(`${API_BASE_URL}/salary/${id}`);
      fetchSalary();
    } catch (err) {
      console.error('Error deleting salary:', err);
    }
  };

  const handleExportCSV = () => {
    const header = [
      "S.No",
      "Employee ID",
      "Name",
      "Bank",
      "Account No",
      "IFSC",
      "Branch",
      "Days Worked",
      "Wage/Day",
      "Total Salary"
    ];

    const rows = salaryData.map((item, index) => [
      index + 1,
      item.employee_id,
      item.name,
      item.bank,
      item.accountNo,
      item.ifsc,
      item.branch,
      item.daysWorked,
      item.wagePerDay,
      item.daysWorked * item.wagePerDay
    ]);

    const csvContent =
      [header, ...rows]
        .map(row => row.join(","))
        .join("\n");

    const blob = new Blob([csvContent], { type: "text/csv" });
    const a = document.createElement("a");
    a.href = URL.createObjectURL(blob);
    a.download = "salary_table.csv";
    a.click();
  };

  const filteredData = salaryData.filter((item) =>
    item.employee_id.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.bank.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="salary-page">
      <h1>Employee Salary Details</h1>

      <form onSubmit={handleSubmit}>
        <input name="employee_id" placeholder="Employee ID" value={formData.employee_id} onChange={handleChange} required />
        <input name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
        <input name="bank" placeholder="Bank" value={formData.bank} onChange={handleChange} required />
        <input name="accountNo" placeholder="Account No" value={formData.accountNo} onChange={handleChange} required />
        <input name="ifsc" placeholder="IFSC" value={formData.ifsc} onChange={handleChange} required />
        <input name="branch" placeholder="Branch" value={formData.branch} onChange={handleChange} required />
        <input name="daysWorked" type="number" placeholder="Days Worked" value={formData.daysWorked} onChange={handleChange} required />
        <input name="wagePerDay" type="number" placeholder="Wage Per Day" value={formData.wagePerDay} onChange={handleChange} required />
        <button type="submit">{editingId ? 'Update Salary' : 'Add Salary'}</button>
      </form>

      <div className="controls">
        <input
          type="text"
          placeholder="Search by ID, name or bank"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleExportCSV}>Export CSV</button>
      </div>

      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Employee ID</th>
            <th>Name</th>
            <th>Bank</th>
            <th>Account No</th>
            <th>IFSC</th>
            <th>Branch</th>
            <th>Days Worked</th>
            <th>Wage/Day</th>
            <th>Total Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((item, index) => (
            <tr key={item._id}>
              <td>{index + 1}</td>
              <td>{item.employee_id}</td>
              <td>{item.name}</td>
              <td>{item.bank}</td>
              <td>{item.accountNo}</td>
              <td>{item.ifsc}</td>
              <td>{item.branch}</td>
              <td>{item.daysWorked}</td>
              <td>{item.wagePerDay}</td>
              <td>{item.daysWorked * item.wagePerDay}</td>
              <td>
                <button className="edit-btn" onClick={() => handleEdit(item)}>Edit</button>
                <button className="delete-btn" onClick={() => handleDelete(item._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Salary;
