import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './CareerApplicants.css';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const ApplicantsTable = () => {
  const [applicants, setApplicants] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchApplicants();
  }, []);

  const fetchApplicants = async () => {
    try {
      const res = await axios.get(`${API_BASE_URL}/applicants`);
      setApplicants(res.data);
    } catch (err) {
      console.error('Error fetching applicants:', err);
    }
  };

  const handleExportCSV = () => {
    const header = ['ID', 'Name', 'Email', 'Phone Number', 'Resume', 'Years of Experience'];
    const rows = applicants.map(applicant => [
      applicant._id,
      applicant.name,
      applicant.email,
      applicant.phone,
      applicant.resume,
      applicant.yearsOfExperience
    ]);
    const csvContent = [header, ...rows].map(row => row.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = 'applicants.csv';
    a.click();
  };

  const filteredApplicants = applicants.filter(applicant =>
    applicant.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (applicant.email && applicant.email.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (applicant.phone && applicant.phone.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="applicants-table-page">
      <h1>Applicants</h1>

      <div className="controls">
        <input
          type="text"
          placeholder="Search by name, email or phone"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleExportCSV}>Export CSV</button>
      </div>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Phone Number</th>
            <th>Resume</th>
            <th>Years of Experience</th>
          </tr>
        </thead>
        <tbody>
          {filteredApplicants.length === 0 ? (
            <tr>
              <td colSpan="6" style={{ textAlign: 'center' }}>No applicants found.</td>
            </tr>
          ) : (
            filteredApplicants.map(applicant => (
              <tr key={applicant._id}>
                <td data-label="ID">{applicant._id}</td>
                <td data-label="Name">{applicant.name}</td>
                <td data-label="Email">{applicant.email}</td>
                <td data-label="Phone Number">{applicant.phone}</td>
                <td data-label="Resume">{applicant.resume}</td>
                <td data-label="Years of Experience">{applicant.yearsOfExperience}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ApplicantsTable;
