import React, { useState } from 'react';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import logo from '../assets/logo1.png';
import './Quotation.css';

const Quotation = () => {
  const [quotationData, setQuotationData] = useState({
    customerName: '',
    customerAddress: '',
    quotationFrom: '',
    quotationTo: '',
    preparedBy: '',
    designation: '',
    items: [
      { product: '', description: '', quantity: 1, price: 0 },
    ],
  });

  const handleItemChange = (index, field, value) => {
    const newItems = [...quotationData.items];
    newItems[index][field] = value;
    setQuotationData({ ...quotationData, items: newItems });
  };

  const handleAddItem = () => {
    setQuotationData({
      ...quotationData,
      items: [...quotationData.items, { product: '', description: '', quantity: 1, price: 0 }],
    });
  };

  const handleRemoveItem = (index) => {
    const newItems = quotationData.items.filter((_, i) => i !== index);
    setQuotationData({ ...quotationData, items: newItems });
  };

  const handleChange = (e) => {
    setQuotationData({ ...quotationData, [e.target.name]: e.target.value });
  };

  const handleGeneratePDF = () => {
    const doc = new jsPDF();

    const img = new Image();
    img.src = logo;

    img.onload = () => {
      doc.addImage(img, 'PNG', 10, 10, 30, 30);

      doc.setGState(new doc.GState({ opacity: 0.05 }));
      doc.addImage(img, 'PNG', 50, 100, 120, 120);
      doc.setGState(new doc.GState({ opacity: 1 }));

      doc.setFontSize(18);
      doc.text('Indian Scientific Aerospace and Robotics', 50, 15);
      doc.setFontSize(10);
      doc.text('339/2, Kurunji Nagar, Valayapatti, Mohanur,', 60, 23);
      doc.text('Namakkal district, Tamil Nadu 637020', 60, 29);
      doc.text('Phone: +91 6374720788 | Email: admin@isaar.in', 60, 35);

      doc.setFontSize(12);
      doc.text(`Date: ${new Date().toLocaleDateString()}`, 170, 28);

    // Wrap Customer Name
const customerNameText = doc.splitTextToSize(`Customer Name: ${quotationData.customerName}`, 170);
doc.text(customerNameText, 20, 54);

// Wrap Address
const addressY = 57 + customerNameText.length * 6; // adjust line spacing if needed
const customerAddressText = doc.splitTextToSize(`Address: ${quotationData.customerAddress}`, 170);
doc.text(customerAddressText, 20, addressY);

// Wrap Quotation From
const fromY = addressY + customerAddressText.length * 6;
const fromText = doc.splitTextToSize(`Quotation From: ${quotationData.quotationFrom}`, 170);
doc.text(fromText, 20, fromY);

// Wrap Quotation To
const toY = fromY + fromText.length * 6;
const toText = doc.splitTextToSize(`Quotation To: ${quotationData.quotationTo}`, 170);
doc.text(toText, 20, toY);

// Calculate next Y for table
let nextStartY = toY + toText.length * 6;


      const tableBody = quotationData.items.map((item, i) => [
        i + 1,
        item.product,
        item.description,
        item.quantity,
        `₹${item.price}`,
        `₹${item.quantity * item.price}`
      ]);

      const subtotal = quotationData.items.reduce((acc, item) => acc + item.quantity * item.price, 0);
      const gst = subtotal * 0.18;
      const total = subtotal + gst;

      autoTable(doc, {
        startY: nextStartY,
        head: [['S.No', 'Product/Item', 'Description', 'Quantity', 'Rate per Unit', 'Total Price']],
        body: tableBody,
        columnStyles: {
          2: { cellWidth: 60 },
        },
        styles: {
          overflow: 'linebreak',
          cellWidth: 'wrap',
        },
      });

      let finalY = doc.lastAutoTable.finalY + 10;

      doc.text(`Subtotal: ₹${subtotal.toFixed(2)}`, 120, finalY);
      finalY += 6;
      doc.text(`GST (18%): ₹${gst.toFixed(2)}`, 120, finalY);
      finalY += 6;
      doc.text(`Grand Total: ₹${total.toFixed(2)}`, 120, finalY);

      finalY += 10;
      doc.text(`Prepared By: ${quotationData.preparedBy || '[Your Name]'}`, 20, finalY);
      finalY += 5;
      doc.text(`Designation: ${quotationData.designation || '[Designation]'}`, 20, finalY);
      finalY += 15;
      doc.text(`Signature: ____________________`, 20, finalY);

      doc.save('quotation.pdf');
    };
  };

  return (
    <div className="quotation">
      <h1>Quotation Page</h1>

      <form className="quotation-form" onSubmit={(e) => e.preventDefault()}>
        <label>Customer Name:</label>
        <input
          type="text"
          name="customerName"
          value={quotationData.customerName}
          onChange={handleChange}
          required
        />

        <label>Customer Address:</label>
        <textarea
          name="customerAddress"
          value={quotationData.customerAddress}
          onChange={handleChange}
          required
        />

        <label>Quotation From:</label>
        <input
          type="text"
          name="quotationFrom"
          value={quotationData.quotationFrom}
          onChange={handleChange}
        />

        <label>Quotation To:</label>
        <input
          type="text"
          name="quotationTo"
          value={quotationData.quotationTo}
          onChange={handleChange}
        />

        {quotationData.items.map((item, index) => (
          <div key={index} className="item-row">
            <label>Product:</label>
            <input
              type="text"
              value={item.product}
              onChange={(e) => handleItemChange(index, 'product', e.target.value)}
              required
            />

            <label>Description:</label>
            <input
              value={item.description}
              onChange={(e) => handleItemChange(index, 'description', e.target.value)}
            />

            <label>Quantity:</label>
            <input
              type="number"
              value={item.quantity}
              onChange={(e) => handleItemChange(index, 'quantity', parseInt(e.target.value))}
              required
            />

            <label>Price per unit:</label>
            <input
              type="number"
              value={item.price}
              onChange={(e) => handleItemChange(index, 'price', parseFloat(e.target.value))}
              required
            />

            {quotationData.items.length > 1 && (
              <button type="button" onClick={() => handleRemoveItem(index)}>
                Remove Item
              </button>
            )}
          </div>
        ))}

        <button type="button" onClick={handleAddItem}>+ Add Item</button>

        <label>Prepared By:</label>
        <input
          type="text"
          name="preparedBy"
          value={quotationData.preparedBy}
          onChange={handleChange}
        />

        <label>Designation:</label>
        <input
          type="text"
          name="designation"
          value={quotationData.designation}
          onChange={handleChange}
        />

        <button type="button" onClick={handleGeneratePDF}>Generate PDF</button>
      </form>
    </div>
  );
};

export default Quotation;

