import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Bills.css';

const API_BASE = process.env.REACT_APP_API_BASE_URL;

const Bills = () => {
  const [productId, setProductId] = useState('');
  const [file, setFile] = useState(null);
  const [bills, setBills] = useState([]);
  const [inventoryItems, setInventoryItems] = useState([]);
  const [editingBillId, setEditingBillId] = useState(null);

  useEffect(() => {
    fetchBills();
    fetchInventory();
  }, []);

  const fetchBills = async () => {
    try {
      const res = await axios.get(`${API_BASE}/bills`);
      setBills(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchInventory = async () => {
    try {
      const res = await axios.get(`${API_BASE}/inventory`);
      setInventoryItems(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSearch = () => {
    const found = inventoryItems.find(item => item.product_id === productId);
    if (found) {
      alert(`Product ID "${productId}" is valid.`);
    } else {
      alert(`Product ID "${productId}" is INVALID.`);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      alert('Please select a PDF file.');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('product_id', productId);
      formData.append('bill', file);

      if (editingBillId) {
        await axios.put(`${API_BASE}/bills/${editingBillId}`, formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        alert('Bill updated successfully!');
        setEditingBillId(null);
      } else {
        await axios.post(`${API_BASE}/bills/upload`, formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        alert('Bill uploaded successfully!');
      }

      setFile(null);
      fetchBills();
    } catch (err) {
      console.error(err);
      alert('Error uploading bill.');
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this bill?')) {
      try {
        await axios.delete(`${API_BASE}/bills/${id}`);
        fetchBills();
      } catch (err) {
        console.error(err);
        alert('Error deleting bill.');
      }
    }
  };

  const handleEdit = (bill) => {
    setProductId(bill.product_id);
    setEditingBillId(bill._id);
    alert('Select a new PDF to replace the existing one.');
  };

  return (
    <div className="bills">
      <h1>Bills</h1>

      <div className="search-section">
        <label>Search Product ID:</label>
        <input
          type="text"
          value={productId}
          onChange={(e) => setProductId(e.target.value)}
          placeholder="Enter Product ID"
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      <form className="upload-section" onSubmit={handleUpload}>
        <label>{editingBillId ? 'Update PDF:' : 'Upload Bill PDF:'}</label>
        <input type="file" accept="application/pdf" onChange={handleFileChange} />
        <button type="submit">{editingBillId ? 'Update PDF' : 'Upload'}</button>
      </form>

      <div className="bill-list">
        <h2>Uploaded Bills</h2>
        {bills.length === 0 ? (
          <p>No bills uploaded yet.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Product ID</th>
                <th>PDF Link</th>
                <th>Uploaded At</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {bills.map((bill) => (
                <tr key={bill._id}>
                  <td data-label="Product ID">{bill.product_id}</td>
                  <td data-label="PDF Link">
                    <a
                      href={`${process.env.REACT_APP_API_BASE_URL.replace('/api', '')}${bill.fileUrl}`}
                      target="_blank"
                      rel="noreferrer"
                    >
                      View PDF
                    </a>
                  </td>
                  <td data-label="Uploaded At">{new Date(bill.uploadedAt).toLocaleString()}</td>
                  <td data-label="Actions">
                    <button className="edit-btn" onClick={() => handleEdit(bill)}>Update</button>
                    <button className="delete-btn" onClick={() => handleDelete(bill._id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Bills;
